pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

contract TodoList {
  uint public taskCount = 0;

  struct Task {
    uint id;
    string content;
    bool completed;
    uint user_id;
  }

  mapping(uint => Task) public tasks;

  event TaskCreated(
    uint id,
    string content,
    bool completed
  );

  event TaskUpdated(
    uint id,
    string content,
    bool completed
  );

  function newTask(string memory _content, uint _user_id) public {
    taskCount ++;
    tasks[taskCount] = Task(taskCount, _content, false, _user_id);
    emit TaskCreated(taskCount, _content, false);
  }

  function editTask(uint _id, string memory _content, uint _user_id, bool _completed) public {
    tasks[_id] = Task(_id, _content, _completed, _user_id);
    emit TaskUpdated(_id, _content, _completed);
  }

  function getAll() public view returns (Task[] memory){
    Task[] memory todos = new Task[](taskCount);
    for (uint i = 0; i < taskCount; i++) {
        todos[i] = tasks[i];
    }
    return todos;
  }

}
